import React from 'react';
import { useSelector } from 'react-redux';
import { selectStocksData, selectAuthor, selectCategory } from './stocksSlice';
import Moment from 'moment';
import Pagination from '@mui/material/Pagination';
import usePagination from "./pagination";
import Stack from '@mui/material/Stack';

function Stocks() {
    const stocks = useSelector(selectStocksData);
    const category = useSelector(selectCategory);
    const author = useSelector(selectAuthor);
    const PER_PAGE = 5;
    const count = Math.ceil(stocks.length / PER_PAGE);
    const handleChange = (e, p) => {
        console.log('e ===> ', e);
        console.log('p ===> ', p);
        // setPage(p);
        _DATA.jump(p);
    };
    const _DATA = usePagination(stocks, PER_PAGE);


  return (
    <div>
        <div>
            <div>
                <label>Category</label>
                <div>
                    {category.length > 0 ?
                        <>
                            {category.map((c, i)=>
                                <input type='checkbox' key={i} name={c[i]} />
                            )}
                        </>
                    :''}
                </div>
                <label>Author</label>
                <div>
                    {author.length > 0 ?
                        <>
                            {author.map((c, i)=>
                                <input type='checkbox' key={i} name={c[i]} />
                            )}
                        </>
                    :''}
                </div>
                <label>Sort By</label>
                <div>
                    <div>
                        <input type='checkbox' key={1} name='Date' value={'Date'} />
                        <img alt=''/>
                    </div>
                    <div>
                        <input type='checkbox' key={2} name='Title' />
                        <img alt=''/>
                    </div>
                </div>
            </div>
        </div>
        <div>
            {_.length > 0 ? _DATA.currentData().map((data, i) => (
            <>
                <div>
                    <div>
                        <div>
                            <img alt='' src={data.url+''+data.image}/>
                        </div>
                        <div>
                            <div>
                                <label>{Moment(data.date).format('LL')}</label>
                                <label>{data.source}</label>
                            </div>
                            <div>
                                <label>{data.title}</label>
                            </div>
                        </div>
                    </div>
                    <div>
                        {data.body}
                    </div>
                    <div>
                        <label>{data.author}</label>
                    </div>
                </div>
            </>
            )): ''}
        </div>
        <div>
            <Stack spacing={2}>
                <Pagination count={count} onChange={handleChange}/>
            </Stack>
        </div>
    </div>
  );
}

export default Stocks;
